#include <stdio.h>
#include "includes/string.h"

void check(void *s, int size)
{
	int i;

	i = 0;
	while (i < size)
	{
		if (((char *)s)[i] != 0)
		{
			printf("F");
			break;
		}
		i++;
	}
	printf("P");
}

typedef struct s_test
{
	int a;
	int b;
	int c;
	int d;
	int e;
	int f;
	char g[1000000];
}	t_test;


int main(void)
{
	char *test1 = "A";
	char *test2 = "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA";
	char *test3 = "";
	int test4 = 1337;
	int test5 = 42;
	int test6 = 8080;
	char test7 = 'a';
	char test8 = 'b';
	char test9 = 'c';
	t_test test10;

	ft_bzero(&test1, sizeof(char) * 1);
	ft_bzero(&test2, sizeof(char) * 25);
	ft_bzero(&test3, sizeof(char) * 40);
	ft_bzero(&test4, sizeof(int));
	ft_bzero(&test5, sizeof(int));
	ft_bzero(&test6, sizeof(int));
	ft_bzero(&test7, sizeof(char));
	ft_bzero(&test8, sizeof(char));
	ft_bzero(&test9, sizeof(char));
	ft_bzero(&test10, sizeof(t_test));

	check(&test1, sizeof(char) * 1);
	check(&test2, sizeof(char) * 25);
	check(&test3, sizeof(char) * 40);
	check(&test4, sizeof(int));
	check(&test5, sizeof(int));
	check(&test6, sizeof(int));
	check(&test7, sizeof(char));
	check(&test8, sizeof(char));
	check(&test9, sizeof(char));
	check(&test10, sizeof(t_test));

	return (0);
}